  const passwordHandler = () => {
  
  }

  const confirmPasswordHandler = () => {
  
  }

  return (
    <div className="signup">
      <div>
        
      </div>
      <form action="" className="signup_form col-lg-8 col-md-12 col-sm-12 " onSubmit={formHandler}>
        <div className="row">
          <div className="col-lg-6 col-sm-12 col-12">
            {/* <label className="label" htmlFor="fname">
              First Name
            </label> */}
            <input type="text" className="form-control mt-3" onChange={nameHandler} placeholder="First Name"/>
          </div>
          <div className="col-lg-6 col-sm-12">
            {/* <label className="label" htmlFor="lname">
              Last Name
            </label> */}
            <input type="text" className="form-control mt-3" onChange={nameHandler} placeholder="Last Name"/>
          </div>
        </div>

        <div className="row">
          <div className="col-lg-6 col-sm-12">
            {/* <label className="label" htmlFor="username">
              Username
            </label> */}
            <input type="text" className="form-control mt-3" onChange={userNameHandler} placeholder="UserName" />
          </div>
          <div className="col-lg-6 col-sm-12">
            {/* <label className="label" htmlFor="email">
              Email
            </label> */}
            <input type="text" className="form-control mt-3" onChange={emailHandler} placeholder="abc@example.com" />
          </div>
        </div>

        <div className="row">
          <div className="col-lg-6 col-sm-12">
            {/* <label className="label" htmlFor="password">
              Password
            </label> */}
            <input type="password" className="form-control mt-3" onChange={passwordHandler} placeholder="Password"/>
          </div>
          <div className="col-lg-6 col-sm-12">
            {/* <label className="label" htmlFor="confirm password">
              Confirm Password
            </label> */}
            <input type="password" className="form-control mt-3" onChange={confirmPasswordHandler} placeholder="Confirm Password" />
          </div>
        </div>

        <div className="row">
          <div className="col-lg-12 mt-2">
            <p className="text-danger"> Already have an Account </p>
          </div>
        </div>

        <div className="row mt-2">
          <div className="col-md-12">
            <button type="submit" className="btn-block signupBtn"> Signup </button>
          </div>

          <div className="col-md-12 text-center mt-3">
            <p className="text-dark"> Already have an Account / <NavLink className="loginLink" to="/login"> Login </NavLink></p>
          </div>
        </div>
      </form>
    </div>
  );
};

export default SignUp;
