import React from "react";
import service1 from "../../images/service-1.webp";
import service2 from "../../images/service-2.webp";
import service3 from "../../images/service-3.webp";

const Services = () => {
  const services = [
    {
      img: service1,
      serviceName: "PROJECT MANAGEMENT",
      time: "1 hr",
      fees: "$220",
      btn: "Book Now",
    },
    {
      img: service2,
      serviceName: "OPERATIONAL CONSULTING",
      time: "1 hr",
      fees: "$170",
      btn: "Book Now",
    },
    {
      img: service3,
      serviceName: "CORPORATE STRATEGY",
      time: "1 hr",
      fees: "@150",
      btn: "Book Now",
    },
  ];

  return (
    <div className="">
      <div className="container mb-5">
        <h1 className="heading text-center"> SERVICES </h1>
      </div>
      <div className="container mb-5">
        <div className="row mt-5 mb-2">
          {services.map((item, idx) => {
            return (
              <>
                <div className="col-md-6 border" key={idx}>
                  <img
                    src={item.img}
                    alt="Team Member 1"
                    className="img-fluid col-lg-12"
                  />
                </div>
                <div className="col-md-6 border">
                  <h1 className="mt-5">{item.serviceName} </h1>
                  <h6 className="">{item.time} </h6>
                  <h6 className="">{item.fees} </h6>
                  <button className="mt-5"> {item.btn} </button>
                </div>
              </>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Services;
