import React, { useState } from "react";
import { Calendar } from "react-calendar";
import { NavLink } from "react-router-dom";
import BookNowNext from "./BookNowNext";
import "./services.css";

const BookNow = () => {

  const [date, setDate] = useState(new Date());

  const onChange = (date) => {
    setDate(date);
  }

  const timing = [
    {
      time: "10:00 am",
    },
    {
      time: "10:30 am",
    },
    {
      time: "11:00 am",
    },
    {
      time: "11:30 am",
    },
    {
      time: "12:00 pm",
    },
    {
      time: "12:30 am",
    },
    {
      time: "01:00 pm",
    },
    {
      time: "01:30 pm",
    },
    {
      time: "02:00 pm",
    },
    {
      time: "02:30 pm",
    },
    {
      time: "03:00 pm",
    },
    {
      time: "03:30 pm",
    },
    {
      time: "04:00 pm",
    },
    {
      time: "04:30 pm",
    },
    {
      time: "05:00 pm",
    },
  ];

  return (
    <div className="container mt-5 mb-5">
      <h1> PROJECT MANAGEMENT </h1>
      <p className="mb-5">
        Check out our availability and book the date and time that works for you
      </p>
      <div className="row">
        <div className="col-lg-4">
        <big className=""> Select a Date </big> <hr/>
        <Calendar/>
        </div>
        <div className="col-lg-3">
        <big className=""> Select a Time </big> <hr/>
          {timing.map((item, index) => {
            return <button className="text-center p-2 px-3 timing" key={index}> {item.time} </button>;
          })}
        </div>
        <div className="col-lg-2">
        <big className=""> Booking Summary </big> <hr/>
        </div>
      </div>
    </div>
  );
};

export default BookNow;
